const SocialIcon: React.FC<{
  type: 'patreon' | 'twitter' | 'itch' | 'reddit' | 'twitch' | 'youtube'
}> = ({ type }) => {
  let icon

  switch (type) {
    case 'patreon':
      icon = PatreonIcon
      break
    case 'twitter':
      icon = TwitterIcon
      break
    case 'itch':
      icon = ItchIcon
      break
    case 'reddit':
      icon = RedditIcon
      break
    case 'twitch':
      icon = TwitchIcon
      break
    case 'youtube':
      icon = YoutubeIcon
      break
    default:
      throw new Error('Unable to get social icon. Missing type.')
  }

  return icon
}

export default SocialIcon
